var app = angular.module('app', []);



app.service('hostingHousesService', ['$http','$q', function ($http, $q) {

    this.getHotels= function () {

        var defer = $q.defer();

        $http.get("./data/select1.json")
            .then(function (response) {
                defer.resolve(response.data);
            });

        return defer.promise;
    }

    this.getHostels = function () {

        var defer = $q.defer()

        $http.get("./data/select2.json")
            .then(function (response) {
                defer.resolve(response.data);
            });

        return defer.promise;
    }

}]);
/******************************
 *
 * select component directive
 *
 ******************************/

app.directive('selectComponent', ['$window', 'hostingHousesService', function ($window, hostingHousesService) {
    return {
        restrict: 'E',
        templateUrl: '../views/templates/selectHotelCmp.html',
        transclude: true,
        scope: {
            dataList: '=?info'
        },
        link: function ($scope, $element, $attributes) {

            $scope.itemSelect = null;

        }
    }
}]);




app.directive('bootselectpicker',['$window','hostingHousesService','$timeout', function ($window, hostingHousesService, $timeout) {
    return {
        restrict: "A",
        link: function(scope, element, attrs, ctrl) {
            $timeout(function() {
                $('.selectpicker').selectpicker({
                    size: 3,
                    width: "300px"
                });

            });

        }
    }
}]);


app.controller('orderCtl', ['$scope','hostingHousesService', function ($scope, hostingHousesService) {

    hostingHousesService.getHotels().then(function(data){
        $scope.hotels = data
    });

    hostingHousesService.getHostels().then(function(data){
        $scope.hostels = data
    });

}]);


